mapper.select:
        SELECT
		ZT.ID, ZT.STRTERMNUM, ZT.APTLID, ZT.STRNETADDR, ZT.DEVICEID, ZT.USINGSTATUS, ZT.STRTERMADDR,
		T.SVCSTATUS, D.STRDEVSN, D.STRDEVMANU, D.IDEVTYPE, D.IDEVCLASS
        FROM Z_TERM ZT INNER JOIN Z_TERMSTATUS T ON T.STRTERMNUM = ZT.STRTERMNUM
        LEFT JOIN Z_DEVICE D ON ZT.DEVICEID = D.ID
        
            
                D.IDEVCLASS != 1
            
            
                AND D.IDEVCLASS = #{deviceClass}
            
            
                AND ZT.STRSUBBRANCHNUM = #{branchnum}
            
            
                AND ZT.STRBRANCHNUM = #{branchnum}

-- 全部 SELECT COUNT(*) TOTALNUM, SUM(IF(T.SVCSTATUS = -1 OR T.SVCSTATUS = 0, 1,0)) ONLINENUM, SUM(IF(T.SVCSTATUS = 4 OR T.SVCSTATUS = 6 OR T.SVCSTATUS = 7, 1,0)) OFFLINENUM, SUM(IF(T.SVCSTATUS = 2 OR T.SVCSTATUS = 3, 1,0)) FAULTNUM, SUM(IF(T.SVCSTATUS = 1 OR T.SVCSTATUS = 5, 1,0)) MAINTAINNUM FROM Z_TERMSTATUS T INNER JOIN Z_TERM ZT ON T.STRTERMNUM = ZT.STRTERMNUM
ZT.STRSUBBRANCHNUM = #{branchnum}
AND ZT.STRBRANCHNUM = #{branchnum}
UNION ALL -- 现金 SELECT COUNT(*) TOTALNUM, SUM(IF(T.SVCSTATUS = -1 OR T.SVCSTATUS = 0, 1,0)) ONLINENUM, SUM(IF(T.SVCSTATUS = 4 OR T.SVCSTATUS = 6 OR T.SVCSTATUS = 7, 1,0)) OFFLINENUM, SUM(IF(T.SVCSTATUS = 2 OR T.SVCSTATUS = 3, 1,0)) FAULTNUM, SUM(IF(T.SVCSTATUS = 1 OR T.SVCSTATUS = 5, 1,0)) MAINTAINNUM FROM Z_TERM ZT INNER JOIN Z_TERMSTATUS T ON T.STRTERMNUM = ZT.STRTERMNUM LEFT JOIN Z_DEVICE D ON ZT.DEVICEID = D.ID WHERE D.IDEVCLASS != 1
AND ZT.STRSUBBRANCHNUM = #{branchnum}
AND ZT.STRBRANCHNUM = #{branchnum}
UNION ALL -- 非现金 SELECT COUNT(*) TOTALNUM, SUM(IF(T.SVCSTATUS = -1 OR T.SVCSTATUS = 0, 1,0)) ONLINENUM, SUM(IF(T.SVCSTATUS = 4 OR T.SVCSTATUS = 6 OR T.SVCSTATUS = 7, 1,0)) OFFLINENUM, SUM(IF(T.SVCSTATUS = 2 OR T.SVCSTATUS = 3, 1,0)) FAULTNUM, SUM(IF(T.SVCSTATUS = 1 OR T.SVCSTATUS = 5, 1,0)) MAINTAINNUM FROM Z_TERM ZT INNER JOIN Z_TERMSTATUS T ON T.STRTERMNUM = ZT.STRTERMNUM LEFT JOIN Z_DEVICE D ON ZT.DEVICEID = D.ID WHERE D.IDEVCLASS = 1
AND ZT.STRSUBBRANCHNUM = #{branchnum}
AND ZT.STRBRANCHNUM = #{branchnum}
