mapper.select:

    SELECT g.* FROM(

SELECT
f.*,(
@b := @b + 1
) AS score
FROM
(
SELECT
teller_id,
sum( sub_seconds ) onlineTime
FROM
(
SELECT
A.teller_id,
A.recordtime,
TIMESTAMPDIFF( SECOND, A.recordtime, B.recordtime ) sub_seconds
FROM
(
SELECT
a.*,(
@i := @i + 1
) AS ord_num
FROM
wbmp_abs_online_time a,(
SELECT
@i := 1
) d
WHERE
a.RTNCODE = '00000000'
AND a.DATA_DT = CURDATE()
ORDER BY
teller_id,
recordtime
) AS A
LEFT JOIN (
SELECT
a.*,(
@j := @j + 1
) AS ord_num
FROM
wbmp_abs_online_time a,(
SELECT
@j := 0
) c
WHERE
RTNCODE = '00000000'
AND DATA_DT = CURDATE()
ORDER BY
teller_id,
recordtime
) AS B ON A.ord_num = B.ord_num
AND A.teller_id = B.teller_id
) e,(
SELECT
@a := 0
) AS tem
WHERE
( @a := @a + 1 )% 2 = 1
GROUP BY
teller_id
) f,(
SELECT
@b := 0
) AS tem1

    ) g
    WHERE teller_id='#{tellerId}';

SELECT SUM(trade_volume) sum FROM wbmp_abs_transinfo
WHERE flag='2' AND DATA_DT=CURDATE() AND concat(substr(ORG_ID,1,6),'01')=#{orgId}
GROUP BY ORG_ID

SELECT rank FROM (
SELECT a.*,(@i := @i + 1) as rank FROM wbmp_abs_transinfo a,(select @i := 0) b
WHERE a.DATA_DT=CURDATE() AND a.flag=3

    and concat(substr(ORG_ID,1,6),'01')=#{orgId}

ORDER BY a.trade_volume DESC) c
WHERE c.TELLER_ID = #{tellerId}

SELECT g.* FROM ( SELECT f.*,( @b := @b + 1 ) AS score FROM ( SELECT teller_id, sum( sub_seconds ) onlineTime FROM ( SELECT A.teller_id, A.ORG_ID, A.INTERFACECODE, A.recordtime, TIMESTAMPDIFF( SECOND, A.recordtime, B.recordtime ) sub_seconds FROM ( SELECT a.*,( @i := @i + 1 ) AS ord_num FROM wbmp_abs_online_time a,( SELECT @i := 1 ) d WHERE a.RTNCODE = '00000000' AND a.DATA_DT = CURDATE() ORDER BY teller_id, recordtime ) AS A LEFT JOIN ( SELECT a.*,( @j := @j + 1 ) AS ord_num FROM wbmp_abs_online_time a,( SELECT @j := 0 ) c WHERE RTNCODE = '00000000' AND DATA_DT = CURDATE() ORDER BY teller_id, recordtime ) AS B ON A.ord_num = B.ord_num AND A.teller_id = B.teller_id ) e WHERE INTERFACECODE = 'NLTTSM_5201' AND sub_seconds IS NOT NULL
and ORG_ID=#{orgId}
GROUP BY teller_id ORDER BY onlineTime DESC ) f,( SELECT @b := 0 ) AS tem1 ) g WHERE teller_id = #{tellerId};
