mapper.select:
SELECT CONVERT (t2.day_str, CHAR) `date`, IFNULL(count(i.id), 0) AS tradeCount FROM ( SELECT @rownum :=@rownum + 1 AS num, date_format(DATE_SUB(NOW(), INTERVAL @rownum DAY),'%Y-%m') AS day_str FROM (SELECT @rownum := - 1) AS r_init, ( SELECT c.id FROM ssamgt.vw_translog c LIMIT 30 ) AS c_init ) t2 LEFT JOIN ssamgt.vw_translog AS i ON ( DATE_FORMAT(i.trans_time, '%Y-%m') = t2.day_str
and substr(i.trans_orgno,1,6) = substr(#{dto.orgId},1,6)
and i.trans_orgno = #{dto.orgId}
and i.term_num = #{dto.termNum}
) GROUP BY t2.day_str

SELECT CONVERT (t2.day_str, CHAR) `date`, IFNULL(count(i.id), 0) AS tradeCount FROM ( SELECT @rownum :=@rownum + 1 AS num, date_format(DATE_SUB(NOW(), INTERVAL @rownum DAY),'%Y-%m-%d') AS day_str FROM (SELECT @rownum := - 1) AS r_init, ( SELECT c.id FROM ssamgt.vw_translog c LIMIT 30 ) AS c_init ) t2 LEFT JOIN ssamgt.vw_translog AS i ON ( DATE_FORMAT(i.trans_time, '%Y-%m-%d') = t2.day_str
and substr(i.trans_orgno,1,6) = substr(#{dto.orgId},1,6)
and i.trans_orgno = #{dto.orgId}
and i.term_num = #{dto.termNum}
) GROUP BY t2.day_str
