mapper.select:
SELECT
    IFNULL(
        (
            SELECT
                INDEX_VAL
            FROM
                WBMP_OPERATE_RACING_INDEX_M
            WHERE
                ORG_ID = #{orgId}
            AND DATE_FORMAT(DATA_DT, '%Y-%m') = #{date}
            AND INDEX_NO = #{id}
        ),
        0
    )
